mapper.select:
select a.balance_detail_id as balanceDetailId, a.account as account, a.balance_type as balanceType, ci.content as balanceName, m.account_date as accountDate, IFNULL(bc.count_type, "1") AS countType, a.balance_main_id as balanceMainId, a.user_id as userId, a.delete_type as deleteType, a.version as version, a.remark as remark
from t_sys_balance_detail a LEFT JOIN t_sys_code_info ci ON ci.code_type_id = 'balanceType' AND ci.value = a.balance_type left join t_sys_balance_main m on m.balance_main_id = a.balance_main_id LEFT JOIN t_sys_balance_count bc on bc.user_id = m.user_id and ci.code_info_id = bc.code_info_id where a.delete_type=0
AND a.balance_type = #{entity.balanceType}
AND a.balance_main_id = #{entity.balanceMainId}
AND a.user_id = #{entity.userId}
order by m.account_date,ci.sort_no

select a.balance_detail_id as balanceDetailId, a.account as account, a.balance_type as balanceType, ci.content as balanceName, m.account_date as accountDate, a.balance_main_id as balanceMainId, a.user_id as userId, a.delete_type as deleteType, a.version as version, a.remark as remark
from t_sys_balance_detail a LEFT JOIN t_sys_code_info ci ON ci.code_type_id = 'balanceType' AND ci.value = a.balance_type left join t_sys_balance_main m on m.balance_main_id = a.balance_main_id where a.delete_type=0
AND a.user_id = #{userId}
AND a.balance_main_id in
#{item}
order by m.account_date,ci.sort_no
