mapper.select:
select a.balance_main_id as balanceMainId, a.account as account, a.account_date as accountDate, a.user_id as userId, a.remark as remark, a.delete_type as deleteType, a.version as version, o.user_name as userName
from t_sys_balance_main a left join t_sys_user o ON o.user_id=a.user_id where 1=1
AND a.delete_type = #{entity.deleteType}
AND a.account_date = #{entity.accountDate}
AND a.account_date >= #{entity.startDate} and a.account_date < #{entity.endDate}
AND a.user_id = #{entity.userId}
AND date_format( a.account_date, '%Y-%m' ) NOT IN ( SELECT c.account_date FROM ( SELECT b.account_date, COUNT( b.account_date ) count FROM ( SELECT date_format( a.account_date, '%Y-%m' ) account_date FROM `t_sys_balance_main` a ORDER BY a.account_date ) b GROUP BY b.account_date HAVING count >= 2 ) c )
AND date_format( a.account_date, '%Y-%m' ) IN ( SELECT c.account_date FROM ( SELECT b.account_date, COUNT( b.account_date ) count FROM ( SELECT date_format( a.account_date, '%Y-%m' ) account_date FROM `t_sys_balance_main` a ORDER BY a.account_date ) b GROUP BY b.account_date HAVING count >= 2 ) c )
order by a.account_date desc
